import React, { useState } from "react";
import "../../styles/MultiUpload.css";

const Step1CSV = () => {
  const [csvFile, setCsvFile] = useState(null);
  const [progress, setProgress] = useState(0);

  const handleFileChange = (e) => {
    setCsvFile(e.target.files[0]);
  };

  const handleUpload = () => {
    if (!csvFile) return alert("Please select a CSV file!");
    setProgress(30);
    setTimeout(() => setProgress(100), 800);
  };

  return (
    <div className="multi-upload-container">
      <h1 className="multi-upload-header">üçé Multi Apple Upload</h1>

      <div className="step-links">
        <a href="/multiupload/step1">Step 1: CSV Upload</a>
        <a href="/multiupload/step2">Step 2: Image Upload</a>
        <a href="/multiupload/step3">Step 3: Review & Submit</a>
      </div>

      <h2 className="step-title">Step 1: Upload your CSV file</h2>

      <div className="upload-box">
        <p>Select your CSV file to begin.</p>
        <input type="file" accept=".csv" onChange={handleFileChange} />
        {csvFile && <p>Selected file: {csvFile.name}</p>}

        <button className="upload-btn" onClick={handleUpload}>
          Upload CSV
        </button>

        {progress > 0 && (
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: `${progress}%` }}></div>
          </div>
        )}
      </div>

      {progress === 100 && (
        <button
          className="next-btn"
          onClick={() => (window.location.href = "/multiupload/step2")}
        >
          Next ‚û°Ô∏è
        </button>
      )}
    </div>
  );
};

export default Step1CSV;
