import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SingleApple.css'; // You'll need to create this CSS file
import { ArrowLeft, Save, Upload } from 'lucide-react';

export default function SingleApple() {
  const navigate = useNavigate();
  const [appleData, setAppleData] = useState({
    cultivar_name: '',
    description: '',
    taste: '',
    texture: '',
    color: '',
    // Add other fields from your schema here
  });
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAppleData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleImageChange = (e) => {
    setImages([...e.target.files]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!appleData.cultivar_name) {
      setError('Cultivar Name is required.');
      return;
    }

    setLoading(true);
    setError('');

    const formData = new FormData();
    // Append the text data as a JSON string
    formData.append('appleData', JSON.stringify(appleData));
    // Append each image file
    images.forEach(image => {
      formData.append('images', image);
    });

    try {
      // Use the 'single-upload' endpoint from your backend
      const response = await axios.post('http://localhost:5000/api/apples/single-upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      alert(`✅ Successfully created ${response.data.apple.cultivar_name}!`);
      navigate('/dashboard'); // Navigate back to the dashboard on success

    } catch (err) {
      const errorMessage = err.response?.data?.message || 'Failed to create apple.';
      setError(errorMessage);
      console.error('Single upload error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="single-apple-container">
      <div className="form-header">
        <h1>Manually Create New Apple</h1>
        <p>Enter the details for a single apple variety and upload its images.</p>
      </div>

      <form onSubmit={handleSubmit} className="single-apple-form">
        <div className="form-section">
          <label htmlFor="cultivar_name">Cultivar Name *</label>
          <input
            type="text"
            id="cultivar_name"
            name="cultivar_name"
            value={appleData.cultivar_name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-section">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            value={appleData.description}
            onChange={handleChange}
            rows="4"
          />
        </div>

        <div className="form-section">
          <label htmlFor="taste">Taste</label>
          <input
            type="text"
            id="taste"
            name="taste"
            value={appleData.taste}
            onChange={handleChange}
          />
        </div>

        <div className="form-section">
          <label htmlFor="images">
            <Upload size={18} /> Upload Images (up to 10)
          </label>
          <input
            type="file"
            id="images"
            name="images"
            onChange={handleImageChange}
            multiple
            accept="image/*"
          />
        </div>

        {error && <p className="error-message">{error}</p>}

        <div className="form-actions">
          <button
            type="button"
            className="btn-secondary"
            onClick={() => navigate('/create-apple')}
          >
            <ArrowLeft size={18} /> Back
          </button>
          <button
            type="submit"
            className="btn-primary"
            disabled={loading}
          >
            {loading ? 'Saving...' : <><Save size={18} /> Save Apple</>}
          </button>
        </div>
      </form>
    </div>
  );
}
