import React, { useState } from "react";
import "./SingleApple.css"; // optional if you have styles

function SingleApple() {
  const [appleName, setAppleName] = useState("");
  const [appleDescription, setAppleDescription] = useState("");
  const [appleImage, setAppleImage] = useState(null);

  const handleFileChange = (e) => {
    setAppleImage(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!appleName || !appleDescription || !appleImage) {
      alert("Please fill all fields!");
      return;
    }
    alert(`‚úÖ Apple "${appleName}" uploaded successfully!`);
  };

  return (
    <div className="create-apple-container">
      <h1 className="page-title">üçé Single Apple Upload</h1>

      <form className="apple-form" onSubmit={handleSubmit}>
        <label>
          Apple Name:
          <input
            type="text"
            value={appleName}
            onChange={(e) => setAppleName(e.target.value)}
            placeholder="Enter apple name"
          />
        </label>

        <label>
          Description:
          <textarea
            value={appleDescription}
            onChange={(e) => setAppleDescription(e.target.value)}
            placeholder="Describe your apple"
          ></textarea>
        </label>

        <label>
          Upload Image:
          <input type="file" onChange={handleFileChange} accept="image/*" />
        </label>

        <button type="submit">Upload Apple</button>
      </form>
    </div>
  );
}

export default SingleApple;
