import React, { useState } from 'react';
import './CreateApple.css';
import axios from 'axios';
import Papa from 'papaparse';
import { useNavigate } from "react-router-dom";

export default function CreateApple() {
  const navigate = useNavigate();
  const [step, setStep] = useState(0);
  const [uploadMethod, setUploadMethod] = useState(null);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [uploadComplete, setUploadComplete] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Handle file selection with CSV validation
  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    const invalidFiles = files.filter(file => !file.name.toLowerCase().endsWith('.csv'));

    if (invalidFiles.length > 0) {
      setError(`Invalid file type: ${invalidFiles.map(f => f.name).join(', ')}. Only CSV files are allowed.`);
      setSelectedFiles([]);
    } else {
      setError('');
      setSelectedFiles(files);
    }
  };

  const handleUpload = () => {
    if (selectedFiles.length === 0) {
      setError('Please select at least one CSV file to upload.');
      return;
    }

    setUploadedFiles((prev) => [...prev, ...selectedFiles]);
    setUploadComplete(true);
    setSelectedFiles([]);
    setError('');
  };

  const handleRestart = () => {
    setStep(0);
    setUploadMethod(null);
    setSelectedFiles([]);
    setUploadedFiles([]);
    setUploadComplete(false);
    setError('');
  };

  // Function to read CSV contents and send to backend
  const submitToBackend = async () => {
    setLoading(true);
    try {
      let apples = [];

      for (const file of uploadedFiles) {
        const text = await file.text();
        const parsed = Papa.parse(text, { header: true, skipEmptyLines: true });

        parsed.data.forEach(row => {
          // Basic validation - adjust fields based on your CSV structure
          if (row.name) {
            apples.push({
              name: row.name,
              color: row.color || 'Unknown',
              origin: row.origin || '',
              taste: row.taste || '',
              texture: row.texture || '',
              // Add more fields as needed
            });
          }
        });
      }

      if (apples.length === 0) {
        alert("‚ùå No valid apple data found in CSV files. Please check the format.");
        setLoading(false);
        return;
      }

      // Send to backend (you'll need to create this endpoint)
      const response = await axios.post("http://localhost:5000/api/apples/bulk-upload", { 
        apples 
      });
      
      console.log('‚úÖ Response:', response.data);
      alert(`üéâ Successfully uploaded ${apples.length} apple(s)!`);
      handleRestart();
      
      // Optionally navigate somewhere after success
      // navigate('/');
      
    } catch (err) {
      console.error('‚ùå Upload error:', err);
      alert(`‚ùå Error: ${err.response?.data?.message || err.message || 'Upload failed'}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="create-apple-container">
      <h1 className="page-title">Create New Apple Resource üçé</h1>

      {/* Timeline */}
      <div className="timeline">
        {['Start', 'Upload', 'Confirmation', 'Review'].map((label, idx) => (
          <div key={idx} className={`timeline-step ${step === idx ? 'active' : ''} ${step > idx ? 'completed' : ''}`}>
            <div className="circle">{step > idx ? '‚úì' : idx + 1}</div>
            <span>{label}</span>
          </div>
        ))}
      </div>

      {/* Step Content */}
      <div className="step-content-card">
        {/* STEP 0: START */}
        {step === 0 && (
          <div className="step">
            <h2>üöÄ Getting Started</h2>
            <div className="instructions-card">
              <h3>How to Use</h3>
              <ul>
                <li>Choose <b>Single Upload</b> to upload one CSV file at a time</li>
                <li>Choose <b>Multiple Upload</b> to upload multiple CSV files in bulk</li>
                <li>Use <b>Download Template</b> to get the sample CSV format</li>
                <li>Make sure your CSV has columns: <b>name, color, origin, taste, texture</b></li>
              </ul>
            </div>

            <div className="upload-options">
              <button
                className="upload-btn single"
                onClick={() => {
                  setUploadMethod('single');
                  setStep(1);
                }}
              >
                Single Upload
              </button>

              <button
                className="upload-btn multiple"
                onClick={() => {
                  setUploadMethod('multiple');
                  setStep(1);
                }}
              >
                Multiple Upload
              </button>

              <button
                className="upload-btn template"
                onClick={() => navigate("/templates")}
              >
                Download Template
              </button>
            </div>
          </div>
        )}

        {/* STEP 1: UPLOAD */}
        {step === 1 && (
          <div className="step">
            <h2>üì§ Upload Files</h2>
            <div className="instructions-card">
              <h3>Upload Instructions</h3>
              <ul>
                <li>Only <b>.csv</b> files are accepted</li>
                <li>Each row should contain apple information</li>
                <li>Required columns: <b>name</b> (minimum)</li>
                <li>Optional columns: <b>color, origin, taste, texture</b></li>
                <li>{uploadMethod === 'multiple' ? 'You can select multiple files' : 'Select one file at a time'}</li>
              </ul>
            </div>

            <input
              type="file"
              accept=".csv"
              multiple={uploadMethod === 'multiple'}
              onChange={handleFileChange}
            />

            {error && (
              <p style={{ color: 'red', fontWeight: '500', marginTop: '10px' }}>
                {error}
              </p>
            )}

            {selectedFiles.length > 0 && (
              <div>
                <h3 style={{ color: '#4caf50', marginTop: '20px' }}>Selected Files:</h3>
                <ul>
                  {selectedFiles.map((file, idx) => (
                    <li key={idx}>{file.name} ({(file.size / 1024).toFixed(2)} KB)</li>
                  ))}
                </ul>
              </div>
            )}

            <div className="navigation-buttons">
              <button className="btn-secondary" onClick={() => setStep(0)}>
                Back
              </button>
              <button 
                className="btn-primary" 
                onClick={handleUpload}
                disabled={selectedFiles.length === 0}
              >
                Upload Files
              </button>
              {uploadComplete && (
                <button className="btn-primary" onClick={() => setStep(2)}>
                  Continue
                </button>
              )}
            </div>
          </div>
        )}

        {/* STEP 2: CONFIRMATION */}
        {step === 2 && (
          <div className="step">
            <h2>‚úÖ Upload Confirmed</h2>
            <p>Your file(s) have been uploaded successfully!</p>
            
            <div style={{ background: '#f8f9fa', padding: '20px', borderRadius: '12px', marginTop: '20px' }}>
              <h3 style={{ color: '#2c3e50', marginBottom: '15px' }}>üì¶ Uploaded Files ({uploadedFiles.length}):</h3>
              <ul>
                {uploadedFiles.map((file, idx) => (
                  <li key={idx}>
                    {file.name} - {(file.size / 1024).toFixed(2)} KB
                  </li>
                ))}
              </ul>
            </div>

            <div className="navigation-buttons">
              <button className="btn-secondary" onClick={() => setStep(1)}>
                Back
              </button>
              <button className="btn-primary" onClick={() => setStep(3)}>
                Review & Submit
              </button>
            </div>
          </div>
        )}

        {/* STEP 3: REVIEW & SUBMIT */}
        {step === 3 && (
          <div className="step">
            <h2>üîç Final Review</h2>
            <p>Please review your uploads before submitting to the database.</p>
            
            <div className="instructions-card">
              <h3>Summary</h3>
              <ul>
                <li>Upload Method: <b>{uploadMethod === 'single' ? 'Single Upload' : 'Multiple Upload'}</b></li>
                <li>Total Files: <b>{uploadedFiles.length}</b></li>
                <li>Total Size: <b>{(uploadedFiles.reduce((acc, f) => acc + f.size, 0) / 1024).toFixed(2)} KB</b></li>
              </ul>
            </div>

            <div style={{ background: '#f8f9fa', padding: '20px', borderRadius: '12px', marginTop: '20px' }}>
              <h3 style={{ color: '#2c3e50', marginBottom: '15px' }}>üìÑ Files to Submit:</h3>
              <ul>
                {uploadedFiles.map((file, idx) => (
                  <li key={idx}>{file.name}</li>
                ))}
              </ul>
            </div>

            <div className="navigation-buttons">
              <button className="btn-secondary" onClick={handleRestart}>
                Start Over
              </button>
              <button 
                className="btn-primary" 
                onClick={submitToBackend}
                disabled={loading}
                style={{ opacity: loading ? 0.6 : 1 }}
              >
                {loading ? '‚è≥ Submitting...' : 'üöÄ Final Submit'}
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Back to Dashboard Link */}
      <div style={{ textAlign: 'center', marginTop: '30px' }}>
        <button 
          onClick={() => navigate('/dashboard')}
          style={{
            background: 'transparent',
            border: '2px solid #e76f51',
            color: '#e76f51',
            padding: '12px 30px',
            borderRadius: '8px',
            fontSize: '16px',
            fontWeight: '600',
            cursor: 'pointer',
            transition: 'all 0.3s'
          }}
          onMouseEnter={(e) => {
            e.target.style.background = '#e76f51';
            e.target.style.color = 'white';
          }}
          onMouseLeave={(e) => {
            e.target.style.background = 'transparent';
            e.target.style.color = '#e76f51';
          }}
        >
          ‚Üê Back to Dashboard
        </button>
      </div>
    </div>
  );
}
